})

// Make Midi Keyboard note value correspond to a musical note
function midiToFreq(number){
const a = 440;
return( a / 32 ) * (2 ** ((number - 9) / 12 ));
}






////////////////////////////////////////////////////////////////////////---MIDI TO BROWSER

if(navigator.requestMIDIAccess) {
    navigator.requestMIDIAccess().then(success, failure)
}

function success(midiAccess) {
    // midiAccess.onstatechange = updateDevices
    midiAccess.addEventListener('statechange', updateDevices)

    const inputs = midiAccess.inputs

    inputs.forEach((input) => {
        // input.onmidimessage = handleInput
        input.addEventListener('midimessage', handleInput)
    })    
}



function handleInput(input) {
    const command = input.data[0];
    const note = input.data[1];
    const velocity = input.data[2];
    
    switch(command) {
        case 144: // notOn
        if (velocity > 0) {
            noteOn(note, velocity);
        } else {
            noteOff(note);
        }
        break;
        case 128: //note off
        noteOff(note);
            break;
    }
}


function noteOn(note, velocity) {
const osc = ctx.createOscillator();

console.log(oscillators)

    // volume and gain
    const oscGain = ctx.createGain();
    oscGain.gain.value = 0.33;

    const velocityGainAmount = (1 /127) * velocity;
    const velocityGain = ctx.createGain();

    velocityGain.gain.value = velocityGainAmount ;   

    osc.type = 'saw';
    osc.frequency.value = midiToFreq(note);

    osc.connect(oscGain);
    oscGain.connect(velocityGain);
    velocityGain.connect(ctx.destination);

    osc.gain = oscGain; 
    
    oscillators[note.toString()] = osc;
    console.log(oscillators)
    osc.start();
}

// when you let go of the key it send a note off message so the sound can stop
function noteOff(note) {
    const osc = oscillators[note.toString()];
    const oscGain = osc.gain;
    
    oscGain.gain.setValueAtTime(oscGain.gain.value, ctx.currentTime);
    oscGain.gain.exponentialRampToValueAtTime(0.0001, ctx.currentTime + 0.03);

    setTimeout(() => {
        osc.stop();
        osc.disconnect();
    }, 20 )
    

    delete oscillators[note.toString()];
    console.log(oscillators);
    console.log(note);
}

function updateDevices(event) { 
    console.log('Name: ${event.port.name}, Brand: ${event.port.manufacturer}, State, ${event.port.state}, Type: ${event.port.type}')
}

