    setupPiano() {
        const piano = document.querySelector("#piano")
        const allNaturalNotes = this.getNaturalNotes(range)
        const pianoWidth = allNaturalNotes.length * whiteKeyWidth
        const SVG = this.createMainSVG(pianoWidth, pianoHeight)


